import React from 'react'
import { StyleSheet, Text, View,Image,TextInput, Alert, ActivityIndicator } from 'react-native'
import LogoName from "../../component/LogoName/index"
import Button from "../../component/Button/index"
import { TouchableOpacity } from 'react-native-gesture-handler'
import { useState } from 'react'
import { event } from 'react-native-reanimated'
import { useEffect } from 'react/cjs/react.development'

const index = ({navigation}) => {

        setTimeout(() => {
            <ActivityIndicator style={{position: 'absolute',
            left: 0,
            right: 0,
            top: 0,
            bottom: 0,
            alignItems: 'center',
            justifyContent: 'center'}} size={30} />
        }, 2000);



    const [userName,setuserName]=useState("");
    const [password,setPassword]=useState("");


    console.info({userName,password})


    const Login=()=>{

    // if(userName ==="admin" && password ==="123"){
    //     Alert.alert("Login SucesFully")
    //     navigation.navigate("Profile")
    // }
    // else if(userName!="admin" || password=="admin"){
    //     Alert.alert("invalid Username")
    // }
    // else if(userName=="admin" || password !="admin"){
    //     Alert.alert("invalid password")
    // }
    // else if(userName !="admin" || password !="admin"){
    //     Alert.alert("Invalid username and Password")
    // }
    // else(
    //     Alert.alert("invalid Detail")
    // )
}



    return (
        <>
        <View style={{flex:1,alignItems:"center",justifyContent:"center",backgroundColor:"#430064",height:"100%"}}>
            <Image
            source={require("../../assets/logo.png") }
            style={{height:330,width:350}}
            />

            <View style={{backgroundColor:"white",width:320,padding:10,alignItems:"center",borderRadius:20}}>
                <TextInput 
                value={userName}
                 style={{fontSize:19}}
                  placeholder="Enter UserName" 
                  onChange={ usname => setuserName(usname.target.value)}
                  />
            </View>
            <View style={{backgroundColor:"white",width:320,padding:10,alignItems:"center",borderRadius:20,marginTop:30}}>
                <TextInput 

                value={password}
                secureTextEntry={true} 
                 style={{fontSize:19,justifyContent:"center"}} placeholder="Password"
                 onChange={ ps=> setPassword(ps.target.value)}
                 />
            </View>

            <View style={{flexDirection:"row",margin:10}}>
            <TouchableOpacity onPress={Login}>
                <View style={{marginTop:20,margin:5,borderRadius:10,borderWidth:1,padding:10,width:140,backgroundColor:"#7400FF"}}>
                    <Text style={{justifyContent:"center",marginLeft:30,fontSize:22,color:"white",borderColor:"#3E0583",fontWeight:"bold"}}>Login</Text>
                </View>
            </TouchableOpacity>
            <TouchableOpacity onPress={()=>{navigation.navigate("SignUp")}}>
                <View style={{marginTop:20,margin:5,borderRadius:10,borderWidth:1,padding:10,width:140,backgroundColor:"yellow"}}>
                    <Text style={{justifyContent:"center",marginLeft:30,fontSize:22,color:"#430064",fontWeight:"bold"}}>SignUp</Text>
                </View>
            </TouchableOpacity>
            </View>

        </View>

         </>
    )
}

export default index

const styles = StyleSheet.create({})
