import React from "react";
import {
  StyleSheet,
  Text,
  View,
  Image,
  TextInput,
  Alert,
  ActivityIndicator,
  ScrollView,
  Dimensions,
} from "react-native";
import LogoName from "../../component/LogoName/index";
import Button from "../../component/Button/index";
import { State, TouchableOpacity } from "react-native-gesture-handler";
import { useState } from "react";
import { color, event } from "react-native-reanimated";
import { useEffect } from "react/cjs/react.development";
import { useDispatch, useSelector } from "react-redux";
import { Password, Username } from "../../redux/loginSlice";
import LinearGradient from "react-native-linear-gradient";

const index = ({ navigation }) => {
  const [userName, setuserName] = useState("");
  const [password, setPassword] = useState("");

  const loginUsername = useSelector((state) => state.signup.username);
  const loginPassword = useSelector((state) => state.signup.Password);

  // const loginusername = useSelector((state) => {
  //   state.signup.username;
  // });
  // const loginPassword = useSelector((state) => {
  //   state.signup.Password;
  // });

  // console.log("Login username==>", loginusername);
  // console.log("Login password==>", loginusername);

  const dispatch = useDispatch();

  console.log("userName", userName);
  console.log("password", password);

  const Login = () => {
    // if (!userName || !password) {
    //   Alert.alert("Info", "Please enter username & password");
    // }
    if (userName == "" && password == "") {
      Alert.alert("Please fill the textinputs first!");
    }
    if (userName == loginUsername && password == loginPassword) {
      // dispatch(Username(userName), Password(password));
      navigation.navigate("Profile");
    } else if (userName != loginUsername && password == loginPassword) {
      Alert.alert("invalid Username");
    } else if (userName == loginUsername && password != loginPassword) {
      Alert.alert("invalid password");
    } else if (userName != loginUsername && password != loginPassword) {
      Alert.alert("invalid UserName and Password");
    }
  };

  return (
    <>
      <ScrollView>
        <View
          style={{
            flex: 1,
            alignItems: "center",
            justifyContent: "center",
            backgroundColor: "#430064",
            height: "100%",
          }}
        >
          <View
            style={{
              marginTop: 100,
              marginLeft: -330,
              alignItems: "flex-start",
              justifyContent: "flex-start",
            }}
          >
            <TouchableOpacity
              onPress={() => {
                navigation.goBack();
              }}
            >
              <Image
                source={require("../../assets/arrow.png")}
                style={{ height: 20, width: 30, marginTop: 70 }}
              />
            </TouchableOpacity>
          </View>
          <Image
            source={require("../../assets/logo.png")}
            style={{ height: 330, width: 350, marginTop: 10 }}
          />

          <View
            style={{
              backgroundColor: "white",
              padding: 40,
              height: "60%",
              borderRadius: 50,
            }}
          >
            <View
              style={{
                backgroundColor: "white",
                width: 320,
                padding: 10,
                alignItems: "center",
                borderRadius: 20,
              }}
            >
              <TextInput
                value={userName}
                placeholderTextColor="white"
                style={{
                  fontSize: 19,
                  padding: 20,
                  color: "white",
                  width: 330,
                  // textAlign: "center",s
                  borderRadius: 20,
                  // backgroundColor: "#430064",
                  backgroundColor: "gray",
                }}
                placeholder="Username"
                onChangeText={(text) => setuserName(text)}
              />
            </View>
            <View
              style={{
                backgroundColor: "white",
                width: 320,
                padding: 10,
                alignItems: "center",
                borderRadius: 20,
              }}
            >
              <TextInput
                style={{
                  fontSize: 19,
                  padding: 20,
                  color: "white",
                  width: 330,
                  // textAlign: "center",s
                  borderRadius: 20,
                  // backgroundColor: "#430064",
                  backgroundColor: "gray",
                }}
                placeholder="password"
                placeholderTextColor="white"
                value={password}
                secureTextEntry={true}
                onChangeText={(text) => setPassword(text)}
              />
            </View>

            <View
              style={{
                // flexDirection: "row",
                margin: 10,
                justifyContent: "center",
                alignItems: "center",
              }}
            >
              <TouchableOpacity
                // style={{  }}
                onPress={Login}
              >
                <View
                  style={{
                    marginTop: 20,
                    margin: 5,
                    borderRadius: 10,
                    borderWidth: 1,
                    padding: 10,
                    width: 140,

                    backgroundColor: "#7400FF",
                  }}
                >
                  <Text
                    style={{
                      justifyContent: "center",
                      marginLeft: 30,
                      fontSize: 22,
                      color: "white",
                      borderColor: "#3E0583",
                      fontWeight: "bold",
                    }}
                  >
                    Login
                  </Text>
                </View>
              </TouchableOpacity>
              <TouchableOpacity>
                <View style={{ marginTop: 30 }}>
                  <Text style={{ fontSize: 17 }}>Forgot Password?</Text>
                </View>
              </TouchableOpacity>
            </View>
          </View>
        </View>
      </ScrollView>
    </>
  );
};

export default index;

const styles = StyleSheet.create({});
