import { StyleSheet, Text, View ,Image,Alert,ActivityIndicator} from 'react-native'
import { useEffect, useState } from 'react/cjs/react.development'
import {logo} from "../../theme/theme"
import * as Progress from 'react-native-progress';
import LinearGradient from 'react-native-linear-gradient';

// import AnimatedLoader from "react-native-animated-loader";
// import { bounce } from 'react-native/Libraries/Animated/src/Easing';

const index = ({navigation}) => {

    useEffect(()=>{
        setTimeout(()=>{

            navigation.navigate("StackNavigation")
        },2000)
    },[]);

    return (
        <View style={styles.container}>
            <Image
            source={logo}
            style={{height:200,width:340,marginTop:"70%",justifyContent:"center"}} />
        <ActivityIndicator size={90}  color={styles.colorcode.color} animating={true} />






        {/* <Progress.Bar
         style={{marginTop:130}}
